","        # 2) column name is not properly formatted but exists in the dataframe","        #   => Include '_' prefix","        # 3) Else: return formatted name","        if new_name == colname:","            # Returns original column","            return colname","        elif new_name in cols:","            return '_' + new_name","        else:","            return new_name","","    return df.rename(columns=_parse_name)"],"stylingDirectives":[[{"start":0,"end":13,"cssClass":"pl-s"}],[{"start":0,"end":0,"cssClass":"pl-s"}],[{"start":0,"end":46,"cssClass":"pl-s"}],[{"start":0,"end":3,"cssClass":"pl-s"}],[{"start":0,"end":6,"cssClass":"pl-k"},{"start":7,"end":9,"cssClass":"pl-s1"}],[],[{"start":0,"end":6,"cssClass":"pl-k"},{"start":7,"end":21,"cssClass":"pl-s1"},{"start":22,"end":24,"cssClass":"pl-k"},{"start":25,"end":31,"cssClass":"pl-s1"}],[],[{"start":0,"end":58,"cssClass":"pl-c"}],[{"start":0,"end":13,"cssClass":"pl-c"}],[{"start":0,"end":58,"cssClass":"pl-c"}],[{"start":0,"end":7,"cssClass":"pl-v"},{"start":8,"end":9,"cssClass":"pl-c1"},{"start":10,"end":16,"cssClass":"pl-s1"},{"start":17,"end":24,"cssClass":"pl-v"}],[{"start":0,"end":14,"cssClass":"pl-v"},{"start":15,"end":16,"cssClass":"pl-c1"},{"start":17,"end":19,"cssClass":"pl-s1"},{"start":20,"end":24,"cssClass":"pl-s1"},{"start":25,"end":29,"cssClass":"pl-en"},{"start":30,"end":37,"cssClass":"pl-v"},{"start":39,"end":53,"cssClass":"pl-s"}],[{"start":0,"end":5,"cssClass":"pl-v"},{"start":6,"end":7,"cssClass":"pl-c1"},{"start":8,"end":20,"cssClass":"pl-s"}],[{"start":0,"end":3,"cssClass":"pl-v"},{"start":4,"end":5,"cssClass":"pl-c1"},{"start":6,"end":18,"cssClass":"pl-s"}],[],[],[{"start":0,"end":58,"cssClass":"pl-c"}],[{"start":0,"end":53,"cssClass":"pl-c"}],[{"start":0,"end":58,"cssClass":"pl-c"}],[{"start":0,"end":3,"cssClass":"pl-k"},{"start":4,"end":12,"cssClass":"pl-en"},{"start":13,"end":16,"cssClass":"pl-s1"}],[{"start":4,"end":76,"cssClass":"pl-s"}],[{"start":0,"end":23,"cssClass":"pl-s"}],[{"star